sys_variable_value.value:
(function execute(inputs, outputs) {

  var instanceUrl = gs.getProperty('glide.servlet.uri'); // get instance url
  var grRecord = new GlideRecord(inputs['tableName']); // gliderecord to given table
  var result = grRecord.get(inputs['sysId']); // get specific record

  // if record found then set link
  if (result) {
  	outputs['link'] = instanceUrl + grRecord.getLink(true); // set link in the output
  } else {
  	outputs['link'] = 'Record not found';
  }

})(inputs, outputs);

