sys_variable_value.value:
(function execute(inputs, outputs) {

  var oldValue; // variable to keep old value
  var newValue; // variable to keep new value
  var variableLabel = inputs['ritm'].variables[inputs['variableName']].getLabel() + ''; // get variable's lable to be used in work notes

  // check if isReferenced parameter is not null or false, if any condition returns true then value is a simple one e.g. string
  if (gs.nil(inputs['isReferenced']) || inputs['isReferenced'] == 'false' || inputs['isReferenced'] == false) {

    oldValue = inputs['ritm'].variables[inputs['variableName']] + ''; // set old value of variable, used "+ ''" to break reference
    newValue = inputs['newValue'] + ''; // set new value varaible, used "+ ''" to break reference

  } else {

    // verify that referenced table was provided
    if (!gs.nil(inputs['referenceTable'])) {

      oldValue = getValue(inputs['referenceTable'],inputs['ritm'].variables[inputs['variableName']]) + ''; // set old value of variable, used "+ ''" to break reference
      newValue = getValue(inputs['referenceTable'],inputs['newValue']) + ''; // set new value varaible, used "+ ''" to break reference

    }

  }

  inputs['ritm'].variables[inputs['variableName']] = inputs['newValue']; // set new value for variable on RITM
  inputs['ritm'].work_notes = 'Value of ' + variableLabel + ' has been changed from "' + oldValue + '" to "' + newValue + '"'; // add work notes with the change
  inputs['ritm'].update(); // update RITM


  /**
  	*
  	* @function getValue
    * @description Gets display value of a record.
    * @param {string} table name	-	name of table where record can be found
    * @param {string} sys_id		-	sys_id of record
    * @return {string|null} string with value or null if not found
    *
    **/
  function getValue(tableName,sys_id) {

    var gr = new GlideRecord(tableName); // glideRecord to pointed table
    var result = gr.get(sys_id); // get record

    // verify if record was found
    if (result) {

      return gr.getDisplayValue(); // return display value

    } 

    return; // return null

  }

})(inputs, outputs);

