sys_variable_value.value:
(function execute(inputs, outputs) {

  var loader = new GlideImportSetLoader();
  var importSetRecord = loader.getImportSetGr(inputs['dataSource']);
  var runLoader = loader.loadImportSetTable(importSetRecord,inputs['dataSource']);
  importSetRecord.state = 'loaded';
  importSetRecord.update();

  outputs['import_set'] = importSetRecord.getUniqueValue();

})(inputs, outputs);

